/* eslint-disable no-undef */
import './App.css';
import { useState } from 'react';
import Slider from './Slider';

let value = 100;

function App () {

  const [currentValue, setCurrentValue] = useState(100);

  const data = (sliderValue, currentValue) => {
    setCurrentValue(sliderValue);
    value = sliderValue
  }


  return (
    <div className="App">
      <div className="content">
        <h1>
          This is my slider, 
          give it a try:) 
        </h1>
        <Slider data={data}/>
        <h2>{value}</h2>
      </div>
    </div>
  );
}

export default App;
