/* eslint-disable no-undef */
import './App.css';
import { useState } from 'react';
import Slider from './Slider';

function App () {

  const [currentValue, setCurrentValue] = useState(100);
  const value = 100;

  const data = (sliderValue, currentValue) => {
    value = setCurrentValue(sliderValue);
  }


  return (
    <div className="App">
      <div className="content">
        <h1>
          This is my slider, 
          give it a try:) 
        </h1>
        <Slider data={data}/>
        <h2>{value}</h2>
      </div>
    </div>
  );
}

export default App;
