/* eslint-disable no-undef */
import './App.css';
import { useState } from 'react';
import ReactSlider from "react-slider";

function App () {

  const [currentValue, setCurrentValue] = useState(100);
  let value = 100;

  const handler = (currentValue) => {
    setCurrentValue(currentValue);
  }

  return (
    <div className="App">
      <div className="content">
        <h1>
          This is my slider, 
          give it a try:) 
        </h1>
        <ReactSlider
        className='mySlider'
        trackClassName="mySlider-track"
        thumbClassName="mySlider-thumb"
        markClassName="mySlider-mark"
        defaultValue={100}
        marks={20}
        max={200}
        min={0}
        value={currentValue}
        onChange={() => setCurrentValue(value)}
        />
        <h2>{currentValue}</h2>
      </div>
    </div>
  );
}

export default App;
